import React, { useState, useEffect } from "react";

import { updateParameter } from "../src/helpers/update";
import {
  MakeCar,
  ModelMake,
  VersionModel,
  VehiclesPage1,
  VehiclesPage2,
  VehiclesPage3,
} from "../src/services/requests";

function Page({
  GetMakeCar,
  GetVehiclesPage1,
  GetVehiclesPage2,
  GetVehiclesPage3,
}) {
  const [modelName, setModelName] = useState();
  const [model, listModel] = useState();
  const [versionId, setVersionId] = useState();
  const [version, listVersion] = useState();
  const [price, setPrice] = useState();
  const [year, setYear] = useState();
  const [f, setF] = useState([]);
  const readModel = async () => {
    const GetModelMake = await ModelMake(modelName);
    listModel(GetModelMake);
  };
  useEffect(() => {
    readModel();
  }, [modelName]);

  const readVersion = async () => {
    const GetVersionModel = await VersionModel(versionId);
    listVersion(GetVersionModel);
  };
  useEffect(() => {
    readVersion();
  }, [versionId]);

  const allVehicles = [
    ...GetVehiclesPage1,
    ...GetVehiclesPage2,
    ...GetVehiclesPage3,
  ];

  const valormaior = parseInt(price) + 19999;

  function searchVehicle() {
    const filtredVehicle = allVehicles.filter((filtred) => {
      return (
        filtred.Make == MakeName &&
        filtred.Price >= price &&
        parseInt(filtred.Price) <= valormaior
      );
    });
    setF(filtredVehicle);
  }

  return (
    <>
      <form>
        <input name="locale" placeholder="Onde:"></input>
        <select className="input" name="distance">
          <option placeholder="Raio:"></option>
        </select>

        <select
          className="input"
          name="make"
          onChange={(e) => {
            setModelName(e.target.value);
          }}
        >
          <option>Todas</option>
          {GetMakeCar.map((make) => {
            return (
              <option value={make.Name} key={make.Name}>
                {make.Name}
              </option>
            );
          })}
        </select>
        <select
          className="input"
          name="model"
          onChange={(e) => {
            setVersionId(e.target.value);
          }}
        >
          <option>Todas</option>
          {model && (
            <>
              {model.map((mode) => {
                return (
                  <option value={mode.Name} key={mode.Name}>
                    {mode.Name}
                  </option>
                );
              })}
            </>
          )}
        </select>
        <select
          className="input"
          name="year"
          onChange={(e) => {
            setYear(e.target.value);
          }}
        >
          <option>Ano Desejado</option>
          <option value="2012">2012</option>
          <option value="2013">2013</option>
          <option value="2014">2014</option>
          <option value="2015">2015</option>
          <option value="2016">2016</option>
          <option value="2017">2017</option>
          <option value="2018">2018</option>
          <option value="2019">2019</option>
          <option value="2020">2020</option>
        </select>
        <select
          className="input"
          name="price"
          onChange={(e) => {
            setPrice(e.target.value);
          }}
        >
          <option>Faixa de Preço</option>
          <option value="0">De 0 á R$ 20.000</option>
          <option value="20000">De R$ 20.000 á R$ 39.999</option>
          <option value="40000">De R$ 40.000 á R$ 59.999</option>
          <option value="60000">De R$ 60.000 á R$ 79.999</option>
          <option value="80000">Mais de R$ 80.000</option>
        </select>
        <select className="input" name="version">
          <option>Todas</option>
          {version && (
            <>
              {version.map((versio) => {
                return (
                  <option value={versio.ID} key={versio.ID}>
                    {versio.Name}
                  </option>
                );
              })}
            </>
          )}
        </select>
        <button>Limpar Filtros</button>
        <button
          onClick={(e) => {
            e.preventDefault();
            searchVehicle();
          }}
        >
          Ver Ofertas
        </button>
      </form>
      <div className="boxes">
        {f.map((vehicle) => {
          return (
            <div className="box">
              <p>Marca: {vehicle.Make}</p>
              <p>Modelo: {vehicle.Model}</p>
              <p>Versão: {vehicle.Version}</p>
              <img src={vehicle.Image} />
              <p>KM: {vehicle.KM}</p>
              <p>Preço: {vehicle.Price}</p>
              <p>
                Ano: {vehicle.YearModel}/ {vehicle.YearFab}
              </p>
              <p>Cor: {vehicle.Color}</p>
            </div>
          );
        })}
      </div>
    </>
  );
}

Page.getInitialProps = async () => {
  const GetMakeCar = await MakeCar();
  const GetVehiclesPage1 = await VehiclesPage1();
  const GetVehiclesPage2 = await VehiclesPage2();
  const GetVehiclesPage3 = await VehiclesPage3();
  return {
    GetMakeCar,
    GetVehiclesPage1,
    GetVehiclesPage2,
    GetVehiclesPage3,
  };
};

export default Page;
