import React, { useState, useEffect } from "react";

import { updateParameter } from "../src/helpers/update";
import {
  MakeCar,
  ModelMake,
  VersionModel,
  Vehicles,
} from "../src/services/requests";

// const Select = ({ label, options, ...rest }) => {
//   console.log(options);
//   return (
//     <label className="label-select">
//       <span>{label}</span>
//       <select {...rest} className="input">
//         {options.map(({ label, Name }) => (
//           <option key={Name} value={Name}>
//             {Name}
//           </option>
//         ))}
//       </select>
//     </label>
//   );
// };
function Page({ GetMakeCar }) {
  const [ModelId, setModelId] = useState();
  const [model, listModel] = useState();
  const readModel = async () => {
    const GetModelMake = await ModelMake(ModelId);
    listModel(GetModelMake);
  };
  useEffect(() => {
    readModel();
  }, [ModelId]);

  return (
    <>
      <form>
        <input></input>
        <select className="input" name="grade_group">
          <option></option>
        </select>
        <select className="input" name="grade_group">
          <option></option>
        </select>

        <select
          className="input"
          name="grade_group "
          onChange={(e) => {
            setModelId(e.target.value);
          }}
        >
          <option>Todas</option>
          {GetMakeCar.map((make) => {
            return <option value={make.ID}>{make.Name}</option>;
          })}
        </select>
        <select className="input" name="grade_group">
          {model && (
            <>
              {model.map((make) => {
                return <option value={make.ID}>{make.Name}</option>;
              })}
            </>
          )}
        </select>
        <select className="input" name="grade_group">
          <option></option>
        </select>
        <select className="input" name="grade_group">
          <option></option>
        </select>
      </form>
    </>
  );
}

Page.getInitialProps = async () => {
  const GetMakeCar = await MakeCar();
  return {
    GetMakeCar,
  };
};

export default Page;
