import Head from "next/head";
import React, { useState, useEffect } from "react";

import Axios from "axios";

function Page() {
  const [loading, setLoading] = useState(true);
  const [data, setData] = useState();
  const [orderListagem, setOrderListagem] = useState();
  const [listagem, setListagem] = useState("todos");
  const url = "https://aguaviva-rp.firebaseio.com/validacao.json";

  function compared(a, b) {
    if (a.nome < b.nome) return -1;
    if (a.nome > b.nome) return 1;
    return 0;
  }
  useEffect(() => {
    if (data) {
      var array = Object.values(data);
      const arrayList = array.sort(compared);
      setOrderListagem(arrayList);
    }
  }, [data]);
  useEffect(() => {
    setLoading(true);
    Axios.get(url).then((res) => {
      setData(res.data);
      setLoading(false);
    });
  }, []);

  return (
    <>
      <Head>
        <title>Igreja Bastista Água Viva - Ribeirão Pires</title>
      </Head>
      <div className="container">
        <div className="header">
          <img src="/images/logo.jpg" />
          <h1>Listagem de controle de presença no culto</h1>
        </div>
        {data && (
          <>
            <div className="select">
              <label>Selecione a listagem do culto que deseja ver</label>
              <select
                onChange={(e) => {
                  setListagem(e.target.value);
                }}
              >
                <option value="todos">Todos</option>
                <option value="sabado">Sabado - 19hs</option>
                <option value="manha">Domingo - 9hs</option>
                <option value="noite">Domingo - 18hs</option>
              </select>
            </div>
            {listagem == "sabado" && (
              <>
                <h1>Sabado - 19hs</h1>
                <table>
                  <thead>
                    <tr>
                      <th>Nome</th>
                      <th>Rede</th>
                    </tr>
                  </thead>
                  <tbody>
                    {orderListagem && (
                      <>
                        {orderListagem.map((individuo) => {
                          if (individuo.culto == "sabado") {
                            return (
                              <tr key={individuo}>
                                <td>
                                  {individuo.nome} {individuo.sobrenome}
                                </td>
                                <td>Pr {individuo.rede}</td>
                              </tr>
                            );
                          }
                        })}
                      </>
                    )}
                  </tbody>
                </table>
              </>
            )}
            {listagem == "manha" && (
              <>
                <h1>Domingo - 9hs</h1>
                <table>
                  <thead>
                    <tr>
                      <th>Nome</th>
                      <th>Rede</th>
                    </tr>
                  </thead>
                  <tbody>
                    {orderListagem && (
                      <>
                        {orderListagem.map((individuo) => {
                          if (individuo.culto == "manha") {
                            return (
                              <tr key={individuo}>
                                <td>
                                  {individuo.nome} {individuo.sobrenome}
                                </td>
                                <td>Pr {individuo.rede}</td>
                              </tr>
                            );
                          }
                        })}
                      </>
                    )}
                  </tbody>
                </table>
              </>
            )}
            {listagem == "noite" && (
              <>
                <h1>Domingo - 18hs</h1>
                <table>
                  <thead>
                    <tr>
                      <th>Nome</th>
                      <th>Rede</th>
                    </tr>
                  </thead>
                  <tbody>
                    {orderListagem && (
                      <>
                        {orderListagem.map((individuo) => {
                          if (individuo.culto == "noite") {
                            return (
                              <tr key={individuo}>
                                <td>
                                  {individuo.nome} {individuo.sobrenome}
                                </td>
                                <td>Pr {individuo.rede}</td>
                              </tr>
                            );
                          }
                        })}
                      </>
                    )}
                  </tbody>
                </table>
              </>
            )}
            {listagem == "todos" && (
              <>
                <h1>Sabado - 19hs</h1>
                <table>
                  <thead>
                    <tr>
                      <th>Nome</th>
                      <th>Rede</th>
                    </tr>
                  </thead>
                  <tbody>
                    {orderListagem && (
                      <>
                        {orderListagem.map((individuo) => {
                          if (individuo.culto == "sabado") {
                            return (
                              <tr key={individuo}>
                                <td>
                                  {individuo.nome} {individuo.sobrenome}
                                </td>
                                <td>Pr {individuo.rede}</td>
                              </tr>
                            );
                          }
                        })}
                      </>
                    )}
                  </tbody>
                </table>

                <h1>Domingo - 9hs</h1>
                <table>
                  <thead>
                    <tr>
                      <th>Nome</th>
                      <th>Rede</th>
                    </tr>
                  </thead>
                  <tbody>
                    {orderListagem && (
                      <>
                        {orderListagem.map((individuo) => {
                          if (individuo.culto == "manha") {
                            return (
                              <tr key={individuo}>
                                <td>
                                  {individuo.nome} {individuo.sobrenome}
                                </td>
                                <td>Pr {individuo.rede}</td>
                              </tr>
                            );
                          }
                        })}
                      </>
                    )}
                  </tbody>
                </table>

                <h1>Domingo - 18hs</h1>
                <table>
                  <thead>
                    <tr>
                      <th>Nome</th>
                      <th>Rede</th>
                    </tr>
                  </thead>
                  <tbody>
                    {orderListagem && (
                      <>
                        {orderListagem.map((individuo) => {
                          if (individuo.culto == "noite") {
                            return (
                              <tr key={individuo}>
                                <td>
                                  {individuo.nome} {individuo.sobrenome}
                                </td>
                                <td>Pr {individuo.rede}</td>
                              </tr>
                            );
                          }
                        })}
                      </>
                    )}
                  </tbody>
                </table>
              </>
            )}
          </>
        )}
        {loading && <p>Carregando...</p>}
        <div className="box-voltar">
          <a href="/" className="button">
            Voltar
          </a>
        </div>
      </div>
    </>
  );
}

export default Page;
